package advent.of.code.y2016.two

import org.scalatest.FunSuite

class KeypadTest extends FunSuite {

  val input1 = "ULL\nRRDDD\nLURDL\nUUUUD"
  val input =
    """|
      |
      |
      |
      |""".stripMargin

  test("testDecipherCode test1") {
    assert("5DB3" == Keypad.decipherCode(input1.split("\n").toList))
  }

  test("testDecipherCode result") {
    assert("B3DB8" == Keypad.decipherCode(input.split("\n").map(_.trim).toList))
  }

}
